import { useState } from "react";

const TodoItem = ({ todo, setRefresh }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editedtask, setEditedtask] = useState(todo.task);

  // const toggleEdit = () => {
  //   setIsEditing(!isEditing);
  // };

  // const handleTaskChange = (event) => {
  //   setUpdatedTask(event.target.value);
  // };

  const updateTodo = () => {
    // Perbarui status tugas di sisi klien
    const updatedTodo = { ...todo, complete: !todo.complete };

    fetch("http://localhost:8000/todos/" + todo.id, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(updatedTodo), // Kirim tugas yang sudah diperbarui
    }).then(() => {
      console.log("Todo updated.");
      setRefresh(true);
    });
  };

  const deleteTodo = () => {
    fetch("http://localhost:8000/todos/" + todo.id, {
      method: "DELETE",
    }).then(() => {
      console.log("Todo deleted.");
      setRefresh(true);
    });
  };

  const handleEditClick = () => {
    setIsEditing(true);
  };

  const handleEditSave = () => {
    todo.task = editedtask;

    fetch("http://localhost:8000/todos/" + todo.id, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(todo),
    }).then(() => {
      console.log("todo edited.");
      setIsEditing(false);
      setRefresh(true);
    });
  };

  const handleEditCancel = () => {
    setEditedtask(todo.task);
    setIsEditing(false);
  };

  return (
    <li className={`${todo.complete ? "checked" : ""}`}>
      {isEditing ? (
        <div>
          <input
            type="text"
            value={editedtask}
            onChange={(e) => setEditedtask(e.target.value)}
          />
          <button onClick={handleEditSave}>Save</button>
          <button onClick={handleEditCancel}>Cancel</button>
        </div>
      ) : (
        <div onClick={updateTodo}>{todo.task}</div>
      )}
      <span className="edit">
        {!isEditing && <button onClick={handleEditClick}>Edit</button>}
      </span>
      <span className="close" onClick={deleteTodo}>
        x
      </span>
    </li>
    // <li className={`${todo.complete ? "checked" : ""} task-container`}>
    //   <div className="list-item" onClick={updateTodo}>
    //     {todo.task}
    //   </div>
    //   <span>{todo.task}</span>
    //   <div className="list-item">
    //   <span className="close" onClick={deleteTodo}>
    //     x
    //   </span>
    //   <span className="close" onClick={toggleEdit}>
    //     i
    //   </span>
    //   {isEditing ? (
    //     <div>
    //       <input type="text" value={updatedTask} onChange={handleTaskChange} />
    //       <button onClick={saveTodo}>Save</button>
    //       <span className="close" onClick={toggleEdit}>
    //         Cancel
    //       </span>
    //     </div>
    //   ) : (
    //   )}
    // </li>
  );
};

export default TodoItem;
