import { useState } from "react";
const TodoItem = ({ todo, setRefresh }) => {
  const [isEditing, setIsEditing] = useState(false); // Menyimpan status apakah sedang dalam mode pengeditan
  const [updatedTask, setUpdatedTask] = useState(todo.task);

  const updateTodo = () => {
    todo.complete = !todo.complete;

    fetch("http://localhost:8000/todos/" + todo.id, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(todo),
    }).then(() => {
      console.log("todo updated.");
      setRefresh(true);
    });
  };

  const deleteTodo = () => {
    fetch("http://localhost:8000/todos/" + todo.id, {
      method: "DELETE",
    }).then(() => {
      console.log("todo deleted.");
      setRefresh(true);
    });
  };

  const editTodo = () => {
    // Buat objek baru yang berisi perubahan yang ingin Anda lakukan pada Todo
    const updatedTodo = {
      ...todo,
      task: updatedTask, // Gunakan nilai dari updatedTask sebagai task yang diperbarui
    };

    fetch("http://localhost:8000/todos/" + todo.id, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(updatedTodo), // Kirim data yang diperbarui sebagai body request
    })
      .then(() => {
        console.log("Todo diperbarui.");
        setRefresh(true);
        setIsEditing(false); // Keluar dari mode pengeditan setelah perubahan disimpan
      })
      .catch((error) => {
        console.error("Gagal memperbarui todo:", error);
      });
  };
  return (
    <>
      <li onClick={updateTodo} className={`${todo.complete ? "checked" : ""}`}>
        <div>{todo.task}</div>
        <span className="close" onClick={deleteTodo}>
          x
        </span>
      </li>
      <li onClick={updateTodo} className={`${todo.complete ? "checked" : ""}`}>
        <div>{todo.task}</div>
        <span className="close" onClick={deleteTodo}>
          i
        </span>
      </li>
      {/* <li className={`${todo.complete ? "checked" : ""}`}>
        {isEditing ? (
          // Tampilkan input untuk mengedit tugas
